import { useContext } from "react";
import LoginForm from "./components/auth/LoginForm";
import { AuthContext } from "./context/AuthContext";

function App() {
  const { user } = useContext(AuthContext);

  return (
    <>
      {!user ? (
        <LoginForm />
      ) : (
        <h2>Welcome {user.userId}! (Bạn đã đăng nhập)</h2>
      )}
    </>
  );
}

export default App;
