import React, { useContext, useState } from "react";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { Button, Typography } from "@mui/material";

export default function UploadFile({ onUploadSuccess }) {
  const { token } = useContext(AuthContext);
  const [status, setStatus] = useState("");
  const [isUploading, setIsUploading] = useState(false);

  const handleFileSelect = async (e) => {
    const file = e.target.files[0];
    if (!file || isUploading) return;

    const formData = new FormData();
    formData.append("file", file);

    try {
      setIsUploading(true);
      setStatus("üïê ƒêang ki·ªÉm tra file...");

      const res = await axios.post(`${process.env.REACT_APP_API_URL}/files/upload`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });

      setStatus("‚úÖ File s·∫°ch! ƒê√£ upload xong");

      // G·ª≠i URL file s·∫°ch l√™n cho component cha x·ª≠ l√Ω
      onUploadSuccess(res.data.fileUrl);

      // Reset sau 3s
      setTimeout(() => setStatus(""), 3000);
    } catch (err) {
      console.error("‚ùå L·ªói upload:", err);
      setStatus(err.response?.data?.message || "‚ùå Upload th·∫•t b·∫°i");
      onUploadSuccess("");
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <>
      <Button variant="outlined" component="label" color="secondary" disabled={isUploading}>
        üìé {isUploading ? "ƒêang upload..." : "Ch·ªçn & Upload File"}
        <input type="file" hidden onChange={handleFileSelect} />
      </Button>

      {status && (
        <Typography variant="body2" sx={{ mt: 1, color: "#a14d52" }}>
          {status}
        </Typography>
      )}
    </>
  );
}
