import { useState, useContext } from "react";
import { Tabs, Tab, Box, Paper, Typography, Button } from "@mui/material";
import { SocketContext } from "../context/SocketContext";
import { AuthContext } from "../context/AuthContext";

import LoginForm from "../components/auth/LoginForm"; // 👈 Import LoginForm
import PrivateChat from "../components/chat/PrivateChat";
import GroupChat from "../components/chat/GroupChat";
import BroadcastChat from "../components/chat/BroadcastChat";

export default function ChatPage() {
  const [tab, setTab] = useState(0);
  const [loggedOut, setLoggedOut] = useState(false); // 👈 trạng thái logout

  const { socket } = useContext(SocketContext);
  const { setUser } = useContext(AuthContext);

  const handleLogout = () => {
    if (socket) socket.disconnect();
    localStorage.clear();
    setUser(null);
    setLoggedOut(true); // 👈 đổi sang trạng thái hiển thị LoginForm
  };

  if (loggedOut) {
    return <LoginForm />;
  }

  return (
    <Box sx={{ maxWidth: 900, mx: "auto", mt: 5, px: 2 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        <Box sx={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <Typography variant="h5" fontWeight={600} gutterBottom>
            💬 KumoChat – Giao tiếp nội bộ
          </Typography>
          <Button
            variant="contained"
            color="error"
            onClick={handleLogout}
            sx={{ ml: 2, whiteSpace: "nowrap" }}
          >
            🚪 Logout
          </Button>
        </Box>

        <Tabs
          value={tab}
          onChange={(_, newValue) => setTab(newValue)}
          indicatorColor="primary"
          textColor="primary"
          centered
          sx={{ mb: 2 }}
        >
          <Tab label="🧍 Nhắn riêng" />
          <Tab label="👥 Nhóm" />
          <Tab label="📢 Broadcast" />
        </Tabs>

        <Box>
          {tab === 0 && <PrivateChat />}
          {tab === 1 && <GroupChat />}
          {tab === 2 && <BroadcastChat />}
        </Box>
      </Paper>
    </Box>
  );
}
