import { useState, useEffect, useContext } from "react";
import { Box, Typography, CircularProgress } from "@mui/material";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import MessageArea from "./MessageArea";

export default function GroupChat() {
  const { token } = useContext(AuthContext);
  const [loading, setLoading] = useState(true);
  const [departments, setDepartments] = useState([]);
  const [selectedDept, setSelectedDept] = useState(null);

  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/departments`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setDepartments(res.data);
      } catch (err) {
        console.error("‚ùå L·ªói l·∫•y ph√≤ng ban:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchDepartments();
  }, [token]);

  if (loading) return <CircularProgress />;
  if (!departments.length) return <Typography>‚ö†Ô∏è Kh√¥ng c√≥ ph√≤ng ban n√†o.</Typography>;

  return (
    <Box>
      <Typography variant="subtitle1">üë• Nh·∫Øn trong m·ªôt ph√≤ng ban</Typography>

      <Box mt={2}>
        <label htmlFor="department-select">Ch·ªçn ph√≤ng ban:</label>
        <select
          id="department-select"
          value={selectedDept?.id || ""}
          onChange={(e) => {
            const dep = departments.find(d => d.id === e.target.value);
            setSelectedDept(dep);
          }}
          style={{ marginLeft: "1rem", padding: "0.5rem" }}
        >
          <option value="">-- Ch·ªçn --</option>
          {departments.map((d) => (
            <option key={d.id} value={d.id}>
              {d.name}
            </option>
          ))}
        </select>
      </Box>

      {selectedDept && (
        <Box mt={2}>
          <MessageArea mode="group" target={selectedDept} />
        </Box>
      )}
    </Box>
  );
}
