import { useEffect, useState, useContext, useCallback } from "react";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { Box, Typography, List, ListItem, ListItemText, TextField, Button } from "@mui/material";
import { encryptMessage } from "../../utils/encryption";
import { decryptPrivateKey } from "../../utils/decryption";

export default function BroadcastChatAdmin() {
  const { token } = useContext(AuthContext);
  const [messages, setMessages] = useState([]);
  const [broadcastInput, setBroadcastInput] = useState("");

  const sendBroadcast = async (message) => {
    const adminUser = JSON.parse(localStorage.getItem("user"));
    const resUsers = await axios.get(`${process.env.REACT_APP_API_URL}/admin/users`, {
      headers: { Authorization: `Bearer ${token}` }
    });

    const allUsers = resUsers.data.filter(u => u.id !== adminUser.id);
    const encryptedAESKeys = [];
    let encryptedContent;

    for (const user of allUsers) {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/admin/users/${user.id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const publicKeyPem = res.data.publicKey;
      const { encryptedContent: encContent, encryptedAESKey } = encryptMessage(message, publicKeyPem);
      if (!encryptedContent) encryptedContent = encContent;

      encryptedAESKeys.push({ userId: user.id, aesKey: encryptedAESKey });
    }

    await axios.post(`${process.env.REACT_APP_API_URL}/admin/broadcast`, {
      encryptedContent,
      encryptedAESKeys
    }, {
      headers: { Authorization: `Bearer ${token}` }
    });

    alert("âœ… Tin nháº¯n broadcast Ä‘Ã£ gá»­i Ä‘áº¿n táº¥t cáº£ ngÆ°á»i dÃ¹ng!");
  };

  const handleSend = async () => {
    if (!broadcastInput.trim()) return;
    await sendBroadcast(broadcastInput);
    setBroadcastInput("");
    fetchBroadcasts(); // refresh sau khi gá»­i
  };

  const fetchBroadcasts = useCallback(async () => {
    const token = localStorage.getItem("token");
    const user = JSON.parse(localStorage.getItem("user"));
    const passphrase = prompt("ðŸ” Nháº­p máº­t kháº©u Ä‘á»ƒ giáº£i mÃ£ tin nháº¯n:");
    if (!passphrase) return;

    // Láº¥y khÃ³a riÃªng Ä‘Ã£ giáº£i mÃ£
    const resKey = await axios.get(`${process.env.REACT_APP_API_URL}/keys/my`, {
      headers: { Authorization: `Bearer ${token}` }
    });

    const privateKeyPem = decryptPrivateKey(resKey.data.encryptedPrivateKey, passphrase);
    if (!privateKeyPem) {
      alert("âŒ Sai máº­t kháº©u hoáº·c khÃ´ng thá»ƒ giáº£i mÃ£ private key");
      return;
    }

    const privateKey = forge.pki.privateKeyFromPem(privateKeyPem);

    // Láº¥y tin nháº¯n
    const res = await axios.get(`${process.env.REACT_APP_API_URL}/admin/messages/broadcast`, {
      headers: { Authorization: `Bearer ${token}` }
    });

    const adminId = user.id;

    // Giáº£i mÃ£ tá»«ng tin nháº¯n
    const decryptedMsgs = await Promise.all(res.data
      .filter(msg => msg.senderId === adminId)
      .map(async (msg) => {
        try {
          const aesKeyBytes = privateKey.decrypt(forge.util.decode64(msg.encryptedAESKey), "RSA-OAEP");
          const decrypted = CryptoJS.AES.decrypt(msg.encryptedContent, aesKeyBytes);
          const plaintext = decrypted.toString(CryptoJS.enc.Utf8);

          return {
            ...msg,
            senderName: msg.sender?.username || "admin",
            decryptedContent: plaintext || "(KhÃ´ng thá»ƒ giáº£i mÃ£)"
          };
        } catch {
          return {
            ...msg,
            senderName: msg.sender?.username || "admin",
            decryptedContent: "(Lá»—i giáº£i mÃ£)"
          };
        }
      })
    );

    setMessages(decryptedMsgs);
  }, []);

  useEffect(() => {
    fetchBroadcasts();
  }, [fetchBroadcasts]);

  return (
    <Box>
      <Typography variant="h6" fontWeight={600} gutterBottom>
        ðŸ“¢ Gá»­i tin nháº¯n Broadcast (Admin)
      </Typography>

      <List dense sx={{ maxHeight: 300, overflow: "auto", bgcolor: "#f4f4f4", mb: 2 }}>
        {messages.map(msg => (
          <ListItem key={msg.id}>
            <ListItemText
              primary={`Tá»«: ${msg.senderName}`}
              secondary={msg.decryptedContent}
            />
          </ListItem>
        ))}
      </List>

      <TextField
        fullWidth
        label="Soáº¡n tin broadcast"
        value={broadcastInput}
        onChange={(e) => setBroadcastInput(e.target.value)}
        multiline
        rows={2}
        sx={{ mb: 1 }}
      />
      <Button variant="contained" onClick={handleSend}>
        Gá»­i Broadcast ðŸ“¤
      </Button>
    </Box>
  );
}
