import { useEffect, useState, useContext, useCallback } from "react";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { Box, Typography, List, ListItem, ListItemText, TextField, Button } from "@mui/material";
import { encryptMessage, encryptAESKeyWithPublicKey } from "../../utils/encryption";
import forge from "node-forge";
import CryptoJS from "crypto-js";

export default function BroadcastChatAdmin() {
  const { token } = useContext(AuthContext);
  const [messages, setMessages] = useState([]);
  const [broadcastInput, setBroadcastInput] = useState("");
  const [password, setPassword] = useState("");
  const [privateKey, setPrivateKey] = useState(null);
  const [adminId, setAdminId] = useState(null);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    setAdminId(user?.id);
  }, []);

  const sendBroadcast = async (message) => {
    const adminUser = JSON.parse(localStorage.getItem("user"));

    // 1. L·∫•y danh s√°ch t·∫•t c·∫£ user tr·ª´ admin
    const resUsers = await axios.get(`${process.env.REACT_APP_API_URL}/admin/users`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    const allUsers = resUsers.data.filter(u => u.id !== adminUser.id);

    const encryptedAESKeys = [];
    let encryptedContent = null;
    let rawAESKeyUsed = null;

    // 2. M√£ h√≥a tin nh·∫Øn cho t·ª´ng user
    for (let i = 0; i < allUsers.length; i++) {
      const user = allUsers[i];

      const res = await axios.get(`${process.env.REACT_APP_API_URL}/admin/users/${user.id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const publicKeyPem = res.data.publicKey;
      const { encryptedContent: encContent, encryptedAESKey, aesKeyRaw } = encryptMessage(message, publicKeyPem, true);

      // L∆∞u n·ªôi dung tin nh·∫Øn v√† raw AES key t·ª´ user ƒë·∫ßu ti√™n
      if (i === 0) {
        encryptedContent = encContent;
        rawAESKeyUsed = aesKeyRaw;
      }

      encryptedAESKeys.push({ userId: user.id, aesKey: encryptedAESKey });
    }

    // 3. M√£ h√≥a AES key cho ch√≠nh admin
    const adminKeyRes = await axios.get(`${process.env.REACT_APP_API_URL}/admin/users/${adminUser.id}`, {
      headers: { Authorization: `Bearer ${token}` }
    });

    const adminPublicKey = adminKeyRes.data.publicKey;
    const adminEncryptedAESKey = encryptAESKeyWithPublicKey(rawAESKeyUsed, adminPublicKey);

    encryptedAESKeys.push({ userId: adminUser.id, aesKey: adminEncryptedAESKey });

    console.log(encryptedAESKeys)

    // 4. G·ª≠i broadcast
    await axios.post(`${process.env.REACT_APP_API_URL}/admin/broadcast`, {
      encryptedContent,
      encryptedAESKeys
    }, {
      headers: { Authorization: `Bearer ${token}` }
    });

    alert("‚úÖ Tin nh·∫Øn broadcast ƒë√£ g·ª≠i ƒë·∫øn t·∫•t c·∫£ ng∆∞·ªùi d√πng!");
    fetchBroadcasts(); // Refresh danh s√°ch sau khi g·ª≠i
  };

  const fetchBroadcasts = useCallback(async () => {
    if (!privateKey || !adminId) return;

    const res = await axios.get(`${process.env.REACT_APP_API_URL}/messages/broadcast`, {
      headers: { Authorization: `Bearer ${token}` }
    });

    const decryptedMsgs = res.data.map(msg => {
      try {
        const myKey = msg.encryptedAESKeys.find(k => k.userId === adminId);
        if (!myKey) throw new Error("Kh√¥ng t√¨m th·∫•y AES key cho admin");

        const aesKeyBytes = privateKey.decrypt(forge.util.decode64(myKey.aesKey), "RSA-OAEP");
        const decrypted = CryptoJS.AES.decrypt(msg.encryptedContent, aesKeyBytes);
        const plaintext = decrypted.toString(CryptoJS.enc.Utf8);

        return {
          ...msg,
          senderName: msg.sender?.username || "admin",
          decryptedContent: plaintext || "(Kh√¥ng th·ªÉ gi·∫£i m√£)"
        };
      } catch (err) {
        return {
          ...msg,
          senderName: msg.sender?.username || "Kh√¥ng r√µ",
          decryptedContent: "(L·ªói gi·∫£i m√£)"
        };
      }
    });

    setMessages(decryptedMsgs);
  }, [token, privateKey, adminId]); // üëà dependencies

  useEffect(() => {
    fetchBroadcasts();
  }, [fetchBroadcasts]);

  const handleSend = async () => {
    if (!broadcastInput.trim()) return;
    await sendBroadcast(broadcastInput);
    setBroadcastInput("");
  };

  const handleLoadPrivateKey = async () => {
    const res = await axios.get(`${process.env.REACT_APP_API_URL}/keys/my`, {
      headers: { Authorization: `Bearer ${token}` }
    });

    const decrypted = decryptPrivateKey(res.data.encryptedPrivateKey, password);
    if (!decrypted) {
      alert("‚ùå Sai m·∫≠t kh·∫©u ho·∫∑c kh√≥a kh√¥ng h·ª£p l·ªá");
    } else {
      setPrivateKey(forge.pki.privateKeyFromPem(decrypted));
      alert("üîì ƒê√£ gi·∫£i m√£ private key th√†nh c√¥ng!");
    }
  };

  return (
    <Box>
      <Typography variant="h6" fontWeight={600} gutterBottom>
        üì¢ G·ª≠i tin nh·∫Øn Broadcast (Admin)
      </Typography>

      {!privateKey && (
        <Box mb={2}>
          <Typography variant="body2">üîê Nh·∫≠p m·∫≠t kh·∫©u ƒë·ªÉ gi·∫£i RSA key:</Typography>
          <Box display="flex" gap={1} mt={1}>
            <TextField
              type="password"
              size="small"
              placeholder="M·∫≠t kh·∫©u"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button variant="outlined" onClick={handleLoadPrivateKey}>
              Gi·∫£i m√£
            </Button>
          </Box>
        </Box>
      )}

      <List dense sx={{ maxHeight: 300, overflow: "auto", bgcolor: "#f4f4f4", mb: 2 }}>
        {messages.map(msg => (
          <ListItem key={msg.id}>
            <ListItemText
              primary={`T·ª´: ${msg.senderName}`}
              secondary={msg.decryptedContent}
            />
          </ListItem>
        ))}
      </List>

      <TextField
        fullWidth
        label="So·∫°n tin broadcast"
        value={broadcastInput}
        onChange={(e) => setBroadcastInput(e.target.value)}
        multiline
        rows={2}
        sx={{ mb: 1 }}
      />
      <Button variant="contained" onClick={handleSend}>
        G·ª≠i Broadcast üì§
      </Button>
    </Box>
  );
}

// üîê H√†m gi·∫£i m√£ private key ƒë√£ m√£ h√≥a b·∫±ng m·∫≠t kh·∫©u
function decryptPrivateKey(encryptedPem, passphrase) {
  try {
    const privateKey = forge.pki.decryptRsaPrivateKey(encryptedPem, passphrase);
    return privateKey ? forge.pki.privateKeyToPem(privateKey) : null;
  } catch {
    return null;
  }
}
