import React, { useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { Button, Typography } from "@mui/material";

export default function UploadFile({ onUploadSuccess }) {
  const { token } = useContext(AuthContext);
  const [status, setStatus] = React.useState("");

  const handleFileSelect = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    try {
      setStatus("üïê ƒêang ki·ªÉm tra file...");
      console.log("debug")
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/files/upload`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });

      setStatus("‚úÖ File s·∫°ch! ƒê√£ upload xong");
      onUploadSuccess(res.data.fileUrl);
    } catch (err) {
      console.error("‚ùå L·ªói upload:", err);
      setStatus(err.response?.data?.message || "‚ùå Upload th·∫•t b·∫°i");
      onUploadSuccess(null);
    }
  };

  return (
    <>
      <Button variant="outlined" component="label" color="secondary">
        üìé Ch·ªçn & Upload File
        <input type="file" hidden onChange={handleFileSelect} />
      </Button>
      {status && (
        <Typography variant="body2" sx={{ mt: 1, color: "#a14d52" }}>
          {status}
        </Typography>
      )}
    </>
  );
}
