import { useState, useEffect, useContext, useMemo } from "react";
import {
  Box,
  Typography,
  CircularProgress,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  IconButton,
  Button,
  List,
  ListItem,
  ListItemText
} from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { SocketContext } from "../../context/SocketContext";
import {
  decryptPrivateKey
} from "../../utils/decryption";
import { encryptMessage } from "../../utils/encryption";

export default function GroupChatAdmin() {
  const { token, user } = useContext(AuthContext);
  const { socket } = useContext(SocketContext);

  const [departments, setDepartments] = useState([]);
  const [selectedDeptId, setSelectedDeptId] = useState("");
  const [groupKey, setGroupKey] = useState(null);
  const [privateKey, setPrivateKey] = useState(null);
  const [password, setPassword] = useState("");
  const [messages, setMessages] = useState([]);
  const [content, setContent] = useState("");
  const [loading, setLoading] = useState(true);

  // const selectedDept = useMemo(
  //   () => departments.find((d) => d.id === selectedDeptId),
  //   [departments, selectedDeptId]
  // );

  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/departments`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setDepartments(res.data);
      } catch (err) {
        console.error("❌ Lỗi lấy danh sách phòng ban:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchDepartments();
  }, [token]);

  useEffect(() => {
    if (!socket) return;

    const handler = (msg) => {
      if (msg.departmentId === selectedDeptId) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("group_message", handler);
    return () => socket.off("group_message", handler);
  }, [socket, selectedDeptId]);

  const handleLoadPrivateKey = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/keys/my`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const decrypted = decryptPrivateKey(res.data.encryptedPrivateKey, password);
      if (!decrypted) alert("❌ Sai mật khẩu hoặc khóa không hợp lệ");
      else {
        setPrivateKey(decrypted);
        alert("🔓 Đã giải mã khóa riêng thành công!");
      }
    } catch (err) {
      console.error("❌ Lỗi tải khóa:", err);
    }
  };

  const fetchGroupKey = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/keys/group/${selectedDeptId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const encryptedBase64 = res.data.encryptedGroupKey;
      const binary = window.atob(encryptedBase64);
      const decrypted = privateKey.decrypt(binary, "RSA-OAEP");
      setGroupKey(decrypted);
    } catch (err) {
      console.error("❌ Không thể lấy group key:", err);
    }
  };

  useEffect(() => {
    if (selectedDeptId && privateKey) {
      fetchGroupKey();
    }
  }, [selectedDeptId, privateKey, fetchGroupKey]);

  const handleSend = async () => {
    if (!content.trim() || !groupKey || !selectedDeptId) return;

    try {
      const { encryptedContent } = encryptMessage(content, groupKey);

      await axios.post(`${process.env.REACT_APP_API_URL}/messages/group`, {
        departmentId: selectedDeptId,
        encryptedContent,
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessages((prev) => [...prev, {
        senderId: user.userId,
        senderName: user.username,
        encryptedContent,
        decryptedContent: content,
      }]);

      setContent("");
    } catch (err) {
      console.error("❌ Gửi tin nhắn nhóm lỗi:", err);
    }
  };

  if (loading) return <CircularProgress />;

  return (
    <Box>
      <Typography variant="h6">👥 Nhắn trong nhóm phòng ban</Typography>

      <FormControl fullWidth sx={{ my: 2 }}>
        <InputLabel>Chọn phòng ban</InputLabel>
        <Select
          value={selectedDeptId}
          onChange={(e) => setSelectedDeptId(e.target.value)}
          label="Chọn phòng ban"
        >
          {departments.map(dep => (
            <MenuItem key={dep.id} value={dep.id}>
              {dep.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>

      {!privateKey && (
        <Box mb={2}>
          <Typography variant="body2">🔐 Nhập mật khẩu để giải mã private key:</Typography>
          <Box display="flex" gap={1} mt={1}>
            <TextField
              type="password"
              size="small"
              placeholder="Mật khẩu"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button variant="outlined" onClick={handleLoadPrivateKey}>
              Giải mã
            </Button>
          </Box>
        </Box>
      )}

      <List dense sx={{ maxHeight: 300, overflow: "auto", bgcolor: "#f0f0f0", mb: 2 }}>
        {messages.map((msg, i) => (
          <ListItem key={i}>
            <ListItemText
              primary={msg.senderId === user.userId ? "Bạn" : msg.senderName || msg.senderId}
              secondary={msg.decryptedContent || "🔒 (chưa giải mã)"}
            />
          </ListItem>
        ))}
      </List>

      <Box display="flex" gap={1}>
        <TextField
          fullWidth
          placeholder="Nhập tin nhắn..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <IconButton onClick={handleSend} color="primary">
          <SendIcon />
        </IconButton>
      </Box>
    </Box>
  );
}
