    alert("‚úÖ G√°n leader th√†nh c√¥ng");
  };

  const filteredUsers = users.filter(user => {
    const inSelectedDept =
      selectedViewDeptId === "" || // t·∫•t c·∫£
      (selectedViewDeptId === "none" && (!user.departments || user.departments.length === 0)) || // ch∆∞a c√≥ ph√≤ng
      user.departments?.some(d => d.department?.id === selectedViewDeptId); // thu·ªôc ph√≤ng c·ª• th·ªÉ

    const matchApproval = !showUnapprovedOnly || !user.isApproved;

    return inSelectedDept && matchApproval;
  });

  const selectedUser = users.find(u => u.id === selectedUserId);

  return (
  <Box sx={{ px: 3, py: 4 }}>

    {/* B·ªô l·ªçc */}
    <Paper elevation={2} sx={{ p: 3, mb: 4 }}>
      <Typography variant="h6" gutterBottom>üîç B·ªô l·ªçc ng∆∞·ªùi d√πng</Typography>
      <Box display="flex" gap={3} flexWrap="wrap">
        <FormControl sx={{ minWidth: 240 }}>
          <InputLabel>L·ªçc theo ph√≤ng ban</InputLabel>
          <Select value={selectedViewDeptId} onChange={(e) => setSelectedViewDeptId(e.target.value)} label="L·ªçc theo ph√≤ng ban">
            <MenuItem value="">T·∫•t c·∫£</MenuItem>
            <MenuItem value="none">Ch∆∞a c√≥</MenuItem>
            {departments.map(dep => (
              <MenuItem key={dep.id} value={dep.id}>{dep.name}</MenuItem>
            ))}
          </Select>
        </FormControl>

        <FormControl sx={{ minWidth: 240 }}>
          <InputLabel>Tr·∫°ng th√°i</InputLabel>
          <Select value={showUnapprovedOnly ? "unapproved" : "all"} onChange={(e) => setShowUnapprovedOnly(e.target.value === "unapproved")} label="Tr·∫°ng th√°i">
            <MenuItem value="all">T·∫•t c·∫£</MenuItem>
            <MenuItem value="unapproved">Ch∆∞a duy·ªát</MenuItem>
          </Select>
        </FormControl>
      </Box>
    </Paper>

    {/* Danh s√°ch ng∆∞·ªùi d√πng */}
    <Paper elevation={2} sx={{ p: 3, mb: 4 }}>
      <Typography variant="h6" gutterBottom>üë• Danh s√°ch ng∆∞·ªùi d√πng ({filteredUsers.length})</Typography>
      <List sx={{ mt: 1, bgcolor: "#f9f9f9", maxHeight: 400, overflow: "auto" }}>
        {filteredUsers.slice(0, 10).map(user => (
          <ListItem key={user.id} divider>
            <ListItemText
              primary={`${user.username} (${user.role}) - Ph√≤ng: ${user.departments?.map(d => d.department?.name).join(", ") || "Ch∆∞a c√≥"}`}
              secondary={`Email: ${user.email} ‚Äì ƒê√£ duy·ªát: ${user.isApproved ? "‚úÖ" : "‚ùå"}`}
            />
            <Box display="flex" gap={1}>
              {!user.isApproved && (
                <Button variant="outlined" size="small" onClick={() => approveUser(user.id)}>Ph√™ duy·ªát</Button>
              )}
              <Button variant="contained" size="small" onClick={() => setSelectedUserId(user.id)}>Ch·ªçn</Button>
            </Box>
          </ListItem>
        ))}
      </List>
      {filteredUsers.length > 10 && (
        <Typography variant="body2" sx={{ mt: 1, fontStyle: "italic" }}>
          ‚ö†Ô∏è Ch·ªâ hi·ªÉn th·ªã 10 ng∆∞·ªùi d√πng ƒë·∫ßu ti√™n.
        </Typography>
      )}
    </Paper>

    {/* Ch·ªçn user ƒë·ªÉ g√°n ph√≤ng ban */}
    {selectedUser && (
      <Paper elevation={2} sx={{ p: 3, mb: 4, bgcolor: "#e3f2fd" }}>
        <Typography variant="h6" gutterBottom>üë§ G√°n ph√≤ng ban cho: {selectedUser.username}</Typography>
        <FormControl fullWidth margin="normal">
          <InputLabel>Ph√≤ng ban</InputLabel>
          <Select value={selectedDept} onChange={(e) => setSelectedDept(e.target.value)}>
            {departments.map(dep => (
              <MenuItem key={dep.id} value={dep.id}>{dep.name}</MenuItem>
            ))}
