        </FormControl>
        <Button variant="contained" sx={{ mt: 2 }} onClick={addUserToDepartment}>‚ûï Th√™m user v√†o ph√≤ng ban</Button>
      </Paper>
    )}

    {/* T·∫°o ph√≤ng ban */}
    <Box mb={4}>
      <Button variant="contained" onClick={createDepartment}>üè¢ T·∫°o ph√≤ng ban</Button>
    </Box>

    {/* G√°n tr∆∞·ªüng ph√≤ng */}
    <Paper elevation={2} sx={{ p: 3, bgcolor: "#fff3e0" }}>
      <Typography variant="h6" gutterBottom>üëë G√°n tr∆∞·ªüng ph√≤ng</Typography>
      <FormControl fullWidth margin="normal">
        <InputLabel>Ph√≤ng ban</InputLabel>
        <Select value={selectedDept} onChange={(e) => setSelectedDept(e.target.value)}>
          {departments.map(dep => (
            <MenuItem key={dep.id} value={dep.id}>{dep.name}</MenuItem>
          ))}
        </Select>
      </FormControl>

      <FormControl fullWidth margin="normal">
        <InputLabel>Username</InputLabel>
        <Select value={leaderId} onChange={(e) => setLeaderId(e.target.value)}>
          {users
            .filter(u => u.role === "USER" && u.departments.some(dep => dep.department.id === selectedDept))
            .map(u => (
              <MenuItem key={u.id} value={u.id}>{u.username}</MenuItem>
