import { useEffect, useState, useContext } from "react";
import axios from "axios";
import forge from "node-forge";
import CryptoJS from "crypto-js";
import { AuthContext } from "../../context/AuthContext";
import { SocketContext } from "../../context/SocketContext";
import { Box, Typography, List, ListItem, ListItemText, TextField, Button } from "@mui/material";

export default function BroadcastChat() {
  const { token } = useContext(AuthContext);
  const { socket } = useContext(SocketContext);
  const [messages, setMessages] = useState([]);
  const [password, setPassword] = useState("");
  const [privateKey, setPrivateKey] = useState(null);

  const loadPrivateKey = async () => {
    const user = JSON.parse(localStorage.getItem("user"));
    const decrypted = decryptPrivateKey(user.encryptedPrivateKey, password);
    if (!decrypted) {
      alert("❌ Sai mật khẩu hoặc private key không hợp lệ");
      return;
    }
    setPrivateKey(forge.pki.privateKeyFromPem(decrypted));
    alert("🔓 Đã giải mã private key thành công!");
  };

  const decryptMessage = (msg) => {
    try {
      const aesKeyBytes = privateKey.decrypt(forge.util.decode64(msg.encryptedAESKey), "RSA-OAEP");
      const decrypted = CryptoJS.AES.decrypt(msg.encryptedContent, aesKeyBytes);
      const plaintext = decrypted.toString(CryptoJS.enc.Utf8);

      console.log(aesKeyBytes);

      return {
        ...msg,
        senderName: msg.sender?.username || "admin",
        decryptedContent: plaintext || "(Không thể giải mã)"
      };
    } catch {
      return {
        ...msg,
        senderName: msg.sender?.username || "Không rõ",
        decryptedContent: "(Lỗi giải mã)"
      };
    }
  };

  // Fetch tin cũ
  useEffect(() => {
    const fetchBroadcasts = async () => {
      if (!privateKey) return;

      const res = await axios.get(`${process.env.REACT_APP_API_URL}/messages/broadcast`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const decryptedMsgs = res.data.map(decryptMessage);
      setMessages(decryptedMsgs);
    };

    fetchBroadcasts();
  }, [token, privateKey]);

  // Tin mới realtime
  useEffect(() => {
    if (!socket || !privateKey) return;

    const handler = (msg) => {
      const decrypted = decryptMessage(msg);
      setMessages((prev) => [...prev, decrypted]);
    };

    socket.on("broadcast_message", handler);
    return () => socket.off("broadcast_message", handler);
  }, [socket, privateKey]);

  return (
    <Box>
      <Typography variant="subtitle1" fontWeight={600}>
        📢 Tin nhắn Broadcast
      </Typography>

      {!privateKey && (
        <Box my={2}>
          <Typography variant="body2">🔐 Nhập mật khẩu để giải RSA key:</Typography>
          <Box display="flex" gap={1} mt={1}>
            <TextField
              type="password"
              size="small"
              placeholder="Mật khẩu"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button variant="outlined" onClick={loadPrivateKey}>
              Giải mã
            </Button>
          </Box>
        </Box>
      )}

      <List dense sx={{ maxHeight: 300, overflow: "auto", bgcolor: "#f4f4f4", mt: 2 }}>
        {messages.map((msg) => (
          <ListItem key={msg.id}>
            <ListItemText
              primary={`Từ: ${msg.senderName}`}
              secondary={msg.decryptedContent}
            />
          </ListItem>
        ))}
      </List>
    </Box>
  );
}

// 🔐 Giải RSA private key đã mã hóa bằng mật khẩu
function decryptPrivateKey(encryptedPem, passphrase) {
  try {
    const privateKey = forge.pki.decryptRsaPrivateKey(encryptedPem, passphrase);
    return privateKey ? forge.pki.privateKeyToPem(privateKey) : null;
  } catch {
    return null;
  }
}
