import { useState, useEffect, useContext } from "react";
import { Box, Typography, List, ListItem, ListItemButton, ListItemText, Divider } from "@mui/material";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import MessageArea from "./MessageArea";

export default function PrivateChat() {
  const { token, user } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [departmentId, setDepartmentId] = useState(null);

  // L·∫•y ph√≤ng ban c·ªßa m√¨nh tr∆∞·ªõc
  useEffect(() => {
    const fetchDepartment = async () => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/auth/me`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        const depId = res.data.department?.id || res.data.departments?.[0]?.id;
        setDepartmentId(depId);
      } catch (err) {
        console.error("‚ùå L·ªói l·∫•y ph√≤ng ban:", err);
      }
    };
    fetchDepartment();
  }, [token]);

  // Sau ƒë√≥ fetch user c√πng ph√≤ng ban
  useEffect(() => {
    const fetchUsers = async () => {
      if (!departmentId) return;
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/departments/${departmentId}/users`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        const filtered = res.data.filter(u => u.id !== user.userId);
        setUsers(filtered);
      } catch (err) {
        console.error("‚ùå L·ªói l·∫•y user trong ph√≤ng ban:", err);
      }
    };
    fetchUsers();
  }, [departmentId, token, user.userId]);

  useEffect(() => {
    const fetchHistory = async () => {
      const res = await axios.get(`${API_URL}/messages/private/${target.id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessages(res.data);
    };
    fetchHistory();
  }, [target]);

  return (
    <Box>
      <Typography variant="subtitle1">üßç Ch·ªçn ng∆∞·ªùi trong ph√≤ng ban ƒë·ªÉ nh·∫Øn tin</Typography>
      <List dense sx={{ maxHeight: 200, overflow: "auto", bgcolor: "#f4f4f4", mt: 1 }}>
        {users.map(u => (
          <ListItem key={u.id} disablePadding>
            <ListItemButton onClick={() => setSelectedUser(u)}>
              <ListItemText primary={u.username} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
      <Divider sx={{ my: 2 }} />
      {selectedUser && (
        <MessageArea mode="private" target={selectedUser} />
      )}
    </Box>
  );
}
