import { useState } from "react";
import { TextField, Button, Box, Typography } from "@mui/material";
import axios from "axios";

export default function RegisterForm() {
  const [form, setForm] = useState({
    username: "",
    email: "",
    password: "",
    publicKey: "",
    encryptedPrivateKey: "",
  });

  const handleChange = (e) => {
    setForm((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/auth/register`, form);
      alert("✅ Đăng ký thành công! Chờ admin duyệt tài khoản nha.");
    } catch (err) {
      console.error(err);
      alert("❌ Đăng ký thất bại!");
    }
  };

  return (
    <Box sx={{ maxWidth: 500, mx: "auto", mt: 8 }}>
      <Typography variant="h5" gutterBottom>Đăng ký tài khoản</Typography>
      <TextField label="Username" name="username" fullWidth margin="normal" onChange={handleChange} />
      <TextField label="Email" name="email" fullWidth margin="normal" onChange={handleChange} />
      <TextField label="Password" name="password" type="password" fullWidth margin="normal" onChange={handleChange} />
      <TextField label="Public Key" name="publicKey" fullWidth margin="normal" onChange={handleChange} />
      <TextField label="Encrypted Private Key" name="encryptedPrivateKey" fullWidth margin="normal" onChange={handleChange} />
      <Button variant="contained" fullWidth sx={{ mt: 2 }} onClick={handleRegister}>Register</Button>
    </Box>
  );
}
