      </FormControl>

      {selectedDept ? (
        <MessageArea mode="group" target={selectedDept} />
      ) : (
        <Typography variant="body2">‚ö†Ô∏è H√£y ch·ªçn m·ªôt ph√≤ng ban ƒë·ªÉ b·∫Øt ƒë·∫ßu nh·∫Øn tin.</Typography>
      )}
