import { useEffect, useState, useContext } from "react";
import { Box, TextField, IconButton, Typography, List, ListItem, ListItemText, Button } from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { SocketContext } from "../../context/SocketContext";
import { encryptMessage } from "../../utils/encryption";
import {
  decryptPrivateKey,
  decryptAESKey,
  decryptContentAES
} from "../../utils/decryption";

export default function MessageArea({ mode, target }) {
  const { token, user } = useContext(AuthContext);
  const { socket } = useContext(SocketContext);

  const [messages, setMessages] = useState([]);
  const [decryptedMessages, setDecryptedMessages] = useState([]);
  const [content, setContent] = useState("");
  const [password, setPassword] = useState("");
  const [privateKey, setPrivateKey] = useState(null);

  // L·∫Øng nghe socket realtime
  useEffect(() => {
    if (!socket) return;

    const handler = (msg) => {
      const match =
        (mode === "private" && msg.senderId === target.id) ||
        (mode === "group" && msg.departmentId === target.id);

      if (match) setMessages((prev) => [...prev, msg]);
    };

    const eventName = mode === "private" ? "private_message" : "group_message";
    socket.on(eventName, handler);
    return () => socket.off(eventName, handler);
  }, [socket, mode, target]);

  // G·ª≠i tin nh·∫Øn
  const handleSend = async () => {
    if (!content.trim()) return;

    try {
      if (mode === "private") {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/keys/public/${target.id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const { encryptedContent, encryptedAESKey } = encryptMessage(content, res.data.publicKey);

        await axios.post(`${process.env.REACT_APP_API_URL}/messages/private`, {
          receiverId: target.id,
          encryptedContent,
          encryptedAESKey,
        }, { headers: { Authorization: `Bearer ${token}` } });

        setMessages((prev) => [...prev, { senderId: user.userId, encryptedContent, encryptedAESKey }]);
      }

      setContent("");
    } catch (err) {
      console.error("‚ùå L·ªói g·ª≠i tin:", err);
    }
  };

  // Gi·∫£i m√£ khi c√≥ tin nh·∫Øn m·ªõi
  useEffect(() => {
    const decryptAll = async () => {
      if (!privateKey) return;

      const decrypted = await Promise.all(messages.map((msg) => {
        try {
          const aesKey = msg.encryptedAESKey
            ? decryptAESKey(msg.encryptedAESKey, privateKey)
            : null;

          const content = aesKey
            ? decryptContentAES(msg.encryptedContent, aesKey)
            : msg.encryptedContent; // fallback n·∫øu kh√¥ng c√≥ AES

          return { ...msg, decryptedContent: content };
        } catch {
          return { ...msg, decryptedContent: "‚ùå Kh√¥ng gi·∫£i m√£ ƒë∆∞·ª£c" };
        }
      }));

      setDecryptedMessages(decrypted);
    };

    decryptAll();
  }, [messages, privateKey]);

  // Nh·∫≠p password ƒë·ªÉ gi·∫£i RSA key
  const handleLoadPrivateKey = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/keys/my`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const decrypted = decryptPrivateKey(res.data.encryptedPrivateKey, password);
      if (!decrypted) {
        alert("‚ùå Sai m·∫≠t kh·∫©u ho·∫∑c kh√≥a kh√¥ng h·ª£p l·ªá");
      } else {
        setPrivateKey(decrypted);
        alert("üîì ƒê√£ gi·∫£i m√£ private key th√†nh c√¥ng!");
      }
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i kh√≥a:", err);
    }
  };

  return (
    <Box>
      {/* Form nh·∫≠p m·∫≠t kh·∫©u gi·∫£i RSA */}
      {!privateKey && (
        <Box mb={2}>
          <Typography variant="body2">üîê Nh·∫≠p m·∫≠t kh·∫©u ƒë·ªÉ gi·∫£i RSA key:</Typography>
          <Box display="flex" gap={1} mt={1}>
            <TextField
              type="password"
              size="small"
              placeholder="M·∫≠t kh·∫©u"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button variant="outlined" onClick={handleLoadPrivateKey}>
              Gi·∫£i m√£
            </Button>
          </Box>
        </Box>
      )}

      {/* Danh s√°ch tin nh·∫Øn */}
      <List dense sx={{ maxHeight: 300, overflow: "auto", bgcolor: "#f0f0f0", mb: 2 }}>
        {decryptedMessages.map((msg, i) => (
          <ListItem key={i}>
            <ListItemText
              primary={msg.senderId === user.userId ? "B·∫°n" : msg.senderId}
              secondary={
                msg.senderId === user.userId
                  ? content // ‚úÖ hi·ªán n·ªôi dung plaintext b·∫°n v·ª´a g·ª≠i
                  : msg.decryptedContent || "üîí Kh√¥ng gi·∫£i m√£ ƒë∆∞·ª£c"
              }
            />
          </ListItem>
        ))}
      </List>

      {/* Nh·∫≠p v√† g·ª≠i tin */}
      <Box display="flex" gap={1}>
        <TextField
          fullWidth
          placeholder="Nh·∫≠p tin nh·∫Øn..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <IconButton onClick={handleSend} color="primary">
          <SendIcon />
        </IconButton>
      </Box>
    </Box>
  );
}
