import axios from "axios";
const urlRegex = /(https?:\/\/[^\s]+)/g;

export async function checkPhishingLinks(content) {
  const urls = content.match(urlRegex);
  if (!urls) return { safe: true };

  for (const url of urls) {
    try {
      const scanRes = await axios.post("https://urlscan.io/api/v1/scan/", {
        url,
        public: "off"
      }, {
        headers: {
          "API-Key": process.env.URLSCAN_API_KEY,
          "Content-Type": "application/json"
        }
      });

      const uuid = scanRes.data.uuid;

      // Wait 5‚Äì10s cho xong ph√¢n t√≠ch
      await new Promise(res => setTimeout(res, 7000));

      const result = await axios.get(`https://urlscan.io/api/v1/result/${uuid}/`);
      const verdict = result.data.verdicts?.overall?.malicious;

      if (verdict) {
        return { safe: false, message: `üö® URL b·ªã ƒë√°nh gi√° l√† ƒë·ªôc h·∫°i: ${url}` };
      }
    } catch (e) {
      console.warn("‚ö†Ô∏è L·ªói khi scan URL:", e.message);
    }
  }

  return { safe: true };
}
