import React, { useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { Button, Typography } from "@mui/material";

export default function UploadFile({ onUploadSuccess }) {
  const { token } = useContext(AuthContext);
  const [selectedFile, setSelectedFile] = useState(null);
  const [status, setStatus] = useState("");

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    setStatus("");
  };

  const handleUpload = async () => {
    if (!selectedFile) return alert("‚ùó H√£y ch·ªçn file!");

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      setStatus("üïê ƒêang ki·ªÉm tra file...");
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/files/upload`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data"
        }
      });

      setStatus("‚úÖ File s·∫°ch! ƒê√£ upload xong");
      onUploadSuccess(res.data.fileUrl); // Tr·∫£ v·ªÅ URL n·∫øu mu·ªën g·ª≠i tin nh·∫Øn
    } catch (err) {
      console.error("‚ùå L·ªói upload:", err);
      setStatus(err.response?.data?.message || "‚ùå Upload th·∫•t b·∫°i");
    }
  };

  return (
    <>
      <input type="file" onChange={handleFileChange} />
      <Button onClick={handleUpload} variant="contained" color="primary" sx={{ ml: 2 }}>
        üì§ Upload
      </Button>
      <Typography variant="body2" sx={{ mt: 1, color: "#a14d52" }}>
        {status}
      </Typography>
    </>
  );
}
