import { createContext, useContext, useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";
import { AuthContext } from "./AuthContext";

export const SocketContext = createContext();

export const SocketProvider = ({ children }) => {
  const { user } = useContext(AuthContext);
  const socketRef = useRef(null);
  const [socketInstance, setSocketInstance] = useState(null); // ✅ lưu đúng socket để context dùng

  useEffect(() => {
    if (user && !socketRef.current) {
      const socket = io(process.env.REACT_APP_SOCKET_URL);
      socketRef.current = socket;
      setSocketInstance(socket);

      socket.emit("join", {
        userId: user.userId,
        departmentIds: user.departmentIds ? [user.departmentIds] : [],
      });

      // 👇 Đánh dấu nếu là reload
      const handleBeforeUnload = (e) => {
        sessionStorage.setItem('reloaded', 'true');
      };

      // 👇 Kiểm tra nếu KHÔNG phải reload thì là đóng tab thật
      const handleUnload = () => {
        const isReload = sessionStorage.getItem('reloaded') === 'true';

        if (!isReload) {
          console.log("🔒 Tab closed — clearing token & disconnecting socket");
          localStorage.removeItem("token");
          if (socketRef.current) {
            socketRef.current.disconnect();
          }
        }

        // reset lại flag để không ảnh hưởng lần sau
        sessionStorage.removeItem('reloaded');
      };

      window.addEventListener("beforeunload", handleBeforeUnload);
      window.addEventListener("unload", handleUnload);

      return () => {
        if (socketRef.current) {
          socketRef.current.disconnect();
          socketRef.current = null;
          setSocketInstance(null);
        }
        window.removeEventListener("beforeunload", handleBeforeUnload);
        window.removeEventListener("unload", handleUnload);
      };
    }
  }, [user]);

  return (
    <SocketContext.Provider value={{ socket: socketInstance }}>
      {children}
    </SocketContext.Provider>
  );
};
