import { useEffect, useState, useContext } from "react";
import {
  Box, Button, Typography, Select, MenuItem, InputLabel, FormControl, List, ListItem, ListItemText, Divider
} from "@mui/material";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";

export default function AdminPanel() {
  const { token } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [newRole, setNewRole] = useState("USER");
  const [selectedDept, setSelectedDept] = useState("");

  // Load all users and departments
  useEffect(() => {
    const fetchData = async () => {
      const [userRes, deptRes] = await Promise.all([
        axios.get("/admin/users", { headers: { Authorization: `Bearer ${token}` } }),
        axios.get("/departments", { headers: { Authorization: `Bearer ${token}` } })
      ]);
      setUsers(userRes.data);
      setDepartments(deptRes.data);
    };
    fetchData();
  }, [token]);

  // Approve user
  const approveUser = async (userId) => {
    await axios.post(`/admin/users/${userId}/approve`, {}, { headers: { Authorization: `Bearer ${token}` } });
    setUsers(users.map(u => u.id === userId ? { ...u, isApproved: true } : u));
  };

  // Change role
  const assignRole = async () => {
    if (!selectedUser) return;
    await axios.post(`/admin/users/${selectedUser}/role`, {
      role: newRole
    }, { headers: { Authorization: `Bearer ${token}` } });
    setUsers(users.map(u => u.id === selectedUser ? { ...u, role: newRole } : u));
  };

  // Assign department
  const assignDepartment = async () => {
    if (!selectedUser || !selectedDept) return;
    await axios.post(`/admin/users/${selectedUser}/department`, {
      departmentId: selectedDept
    }, { headers: { Authorization: `Bearer ${token}` } });
  };

  // Create group (department)
  const createGroup = async () => {
    const name = prompt("Tên phòng ban:");
    if (!name) return;
    await axios.post("/departments", { name }, { headers: { Authorization: `Bearer ${token}` } });
    const updated = await axios.get("/departments", { headers: { Authorization: `Bearer ${token}` } });
    setDepartments(updated.data);
  };

  return (
    <Box>
      <Typography variant="h6" mb={2}>👑 Bảng điều khiển Admin</Typography>

      <Button variant="contained" onClick={createGroup}>➕ Tạo phòng ban mới</Button>

      <List sx={{ mt: 2, bgcolor: "#f4f4f4" }}>
        {users.map(user => (
          <ListItem key={user.id} divider>
            <ListItemText primary={`${user.username} (${user.role})`} secondary={user.email} />
            {!user.isApproved && (
              <Button size="small" onClick={() => approveUser(user.id)}>✅ Phê duyệt</Button>
            )}
            <Button size="small" onClick={() => setSelectedUser(user.id)}>⚙️ Chỉnh sửa</Button>
          </ListItem>
        ))}
      </List>

      {selectedUser && (
        <Box mt={3} p={2} bgcolor="#e3f2fd" borderRadius={2}>
          <Typography variant="subtitle1">👤 Chỉnh sửa user: {selectedUser}</Typography>

          <FormControl fullWidth margin="normal">
            <InputLabel>Vai trò</InputLabel>
            <Select value={newRole} label="Vai trò" onChange={(e) => setNewRole(e.target.value)}>
              <MenuItem value="USER">USER</MenuItem>
              <MenuItem value="LEADER">LEADER</MenuItem>
              <MenuItem value="ADMIN">ADMIN</MenuItem>
            </Select>
            <Button sx={{ mt: 1 }} onClick={assignRole}>🎖 Gán quyền</Button>
          </FormControl>

          <FormControl fullWidth margin="normal">
            <InputLabel>Phòng ban</InputLabel>
            <Select value={selectedDept} label="Phòng ban" onChange={(e) => setSelectedDept(e.target.value)}>
              {departments.map(dep => (
                <MenuItem key={dep.id} value={dep.id}>{dep.name}</MenuItem>
              ))}
            </Select>
            <Button sx={{ mt: 1 }} onClick={assignDepartment}>🏢 Gán vào phòng ban</Button>
          </FormControl>
        </Box>
      )}
    </Box>
  );
}
